    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked
        // TODO add your handling code here:
        if (jTextField1.getText().equals("")) {
            JOptionPane.showMessageDialog(null, "Inserire termini da cercare nel campo di ricerca!", "Errore ricerca", 0);
            return;
        }
        motori.ricerca(jTextField1.getText(), motoreCorrente, (Utente) Sistema.getSistema().getStatoCorrente());
    }//GEN-LAST:event_jButton1MouseClicked

