    private void jButton5MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton5MouseClicked
        // TODO add your handling code here:
        if (jTextField4.getText().equals("")) {
            JOptionPane.showMessageDialog(null, "Inserire il nome del premio desiderato!", "Errore visualizzazione premio", 0);
            return;
        }
        if (Catalogo.getCatalogo().search(jTextField4.getText()) == null) {
            JOptionPane.showMessageDialog(null, "Errore: nome del premio non riconosciuto!", "Errore nome premio", 0);
            return;
        }
        jPanel3.setVisible(false);
        jPanel4.setVisible(true);
        Catalogo.getCatalogo().search(jTextField4.getText()).visualizzaPremio();
    }//GEN-LAST:event_jButton5MouseClicked

    private void jButton8MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton8MouseClicked
        // TODO add your handling code here:
        Catalogo.getCatalogo().search(jTextField4.getText()).richiediPremio((Utente) Sistema.getSistema().getStatoCorrente());
    }//GEN-LAST:event_jButton8MouseClicked

